// src/components/CandidateDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const CandidateDashboard = () => {
    const [jobs, setJobs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchJobs = async () => {
            try {
                const response = await axios.get('http://localhost:5000/admin/all-job');
                setJobs(response.data.jobs);
                setLoading(false);
            } catch (err) {
                setError('Failed to fetch jobs');
                setLoading(false);
                console.error(err);
            }
        };

        fetchJobs();
    }, []);

    const handleApply = async () => {
        const user = getParams()
        const response = axios.post("http://localhost:5000/admin/all-job",)
    }

    if (loading) {
        return <div className="text-center py-4">Loading...</div>;
    }

    if (error) {
        toast.error(error);
        return <div className="text-center py-4">{error}</div>;
    }

    return (
        <div className="min-h-screen bg-gray-100 p-6">
            <ToastContainer />
            <h1 className="text-3xl font-bold mb-6">Available Jobs</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {jobs.map((job) => (
                    <div key={job._id} className="bg-white p-4 rounded-lg shadow-md">
                        <h2 className="text-xl font-semibold mb-2">{job.title}</h2>
                        <p className="text-gray-600 mb-2">Company: {job.company}</p>
                        <p className="text-gray-600 mb-2">Category: {job.category}</p>
                        <p className="text-gray-600 mb-2">Type: {job.type}</p>
                        <p className="text-gray-600 mb-2">Experience Required: {job.experienceRequired}</p>
                        <p className="text-gray-600 mb-2">Salary: {job.salary}</p>
                        <p className="text-gray-800">{job.description}</p>
                        <button onClick={handleApply}>Apply</button>
                    </div>
                ))}
            </div>

        </div>
    );
};

export default CandidateDashboard;
