import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useParams } from 'react-router-dom';

function ViewApplications() {
    const { jobId } = useParams();
    const [candidates, setCandidates] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Replace with your actual API endpoint
        const fetchCandidates = async () => {
            try {
                setLoading(true);
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:5000/application/candidates/${jobId}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                }); // Replace 'someJobId' with an actual job ID
                setCandidates(response.data.candidates);
                toast.success("Candidates fetched successfully!");
            } catch (err) {
                setError(err.message);
                toast.error("Failed to fetch candidates!");
            } finally {
                setLoading(false);
            }
        };

        fetchCandidates();
    }, []); // Empty dependency array means this effect runs once when component mounts

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    const handleViewMore = (candidateId) => {
        // Handle fetching more detailed information when a candidate is selected
        console.log(`Fetch more details for candidate ID: ${candidateId}`);
        // For example, open a modal or navigate to a detailed view
    };

    return (
        <div>
            <h1>Candidates</h1>
            {candidates.length === 0 ? (
                <p>No candidates found.</p>
            ) : (
                <ul>
                    {candidates.map(candidate => (
                        <li key={candidate._id}>
                            <h2>Candidate ID: {candidate._id}</h2>
                            <p><strong>User ID:</strong> {candidate.user || 'N/A'}</p> {/* Display user ID */}
                            <p><strong>Phone Number:</strong> {candidate.phoneNumber || 'N/A'}</p>
                            <p><strong>Address:</strong> {candidate.address || 'N/A'}</p>
                            <p><strong>Resume:</strong> {candidate.resume ? <a href={candidate.resume} target="_blank" rel="noopener noreferrer">View Resume</a> : 'N/A'}</p>
                            <p><strong>LinkedIn Profile:</strong> {candidate.linkedinProfile ? <a href={candidate.linkedinProfile} target="_blank" rel="noopener noreferrer">View Profile</a> : 'N/A'}</p>
                            <p><strong>Portfolio:</strong> {candidate.portfolio ? <a href={candidate.portfolio} target="_blank" rel="noopener noreferrer">View Portfolio</a> : 'N/A'}</p>
                            <p><strong>Skills:</strong> {candidate.skills.length > 0 ? candidate.skills.join(', ') : 'N/A'}</p>

                            <div>
                                <strong>Experience:</strong>
                                {candidate.experience.length > 0 ? (
                                    <ul>
                                        {candidate.experience.map((exp, index) => (
                                            <li key={index}>
                                                <p><strong>Company:</strong> {exp.company}</p>
                                                <p><strong>Role:</strong> {exp.role}</p>
                                                <p><strong>Start Date:</strong> {new Date(exp.startDate).toLocaleDateString()}</p>
                                                <p><strong>End Date:</strong> {exp.endDate ? new Date(exp.endDate).toLocaleDateString() : 'Present'}</p>
                                                <p><strong>Description:</strong> {exp.description}</p>
                                            </li>
                                        ))}
                                    </ul>
                                ) : (
                                    <p>No experience listed.</p>
                                )}
                            </div>

                            <div>
                                <strong>Education:</strong>
                                {candidate.education.length > 0 ? (
                                    <ul>
                                        {candidate.education.map((edu, index) => (
                                            <li key={index}>
                                                <p><strong>Institution:</strong> {edu.institution}</p>
                                                <p><strong>Degree:</strong> {edu.degree}</p>
                                                <p><strong>Start Date:</strong> {new Date(edu.startDate).toLocaleDateString()}</p>
                                                <p><strong>End Date:</strong> {edu.endDate ? new Date(edu.endDate).toLocaleDateString() : 'Present'}</p>
                                                <p><strong>Description:</strong> {edu.description}</p>
                                            </li>
                                        ))}
                                    </ul>
                                ) : (
                                    <p>No education listed.</p>
                                )}
                            </div>

                            <p><strong>Status:</strong> {candidate.status}</p>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}


export default ViewApplications;
