import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const CandidateDashboard = () => {
    const [jobs, setJobs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchJobs = async () => {
            try {
                const response = await axios.get('http://localhost:5000/jobs');
                setJobs(response.data.jobs);
                setLoading(false);
            } catch (err) {
                setError('Failed to fetch jobs');
                toast.error('Failed to fetch jobs');
                setLoading(false);
            }
        };

        fetchJobs();
    }, []);

    if (loading) {
        return <div className="text-center p-4">Loading...</div>;
    }

    return (
        <div className="p-4">
            <h1 className="text-2xl font-bold mb-6">Available Jobs</h1>
            {error && <div className="text-red-500 mb-4">{error}</div>}
            {jobs.length === 0 ? (
                <div className="text-center">No jobs available</div>
            ) : (
                <div className="space-y-4">
                    {jobs.map((job) => (
                        <div key={job._id} className="bg-white p-4 rounded-lg shadow-md">
                            <h2 className="text-xl font-semibold">{job.title}</h2>
                            <p className="text-gray-600">{job.description}</p>
                            <div className="mt-4">
                                <span className="font-semibold">Location:</span> {job.location}
                            </div>
                            <div className="mt-2">
                                <span className="font-semibold">Category:</span> {job.category}
                            </div>
                            <div className="mt-4">
                                <button
                                    className="px-4 py-2 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700"
                                    onClick={() => alert(`Applied for ${job.title}`)}
                                >
                                    Apply
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
            <ToastContainer />
        </div>
    );
};

export default CandidateDashboard;
