import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ViewApplications = () => {
    const { jobId } = useParams();
    const [applications, setApplications] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchApplications = async () => {
            try {
                setLoading(true);
                const response = await axios.get(`http://localhost:5000/application/candidates/${jobId}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setApplications(response.data.applications);
                toast.success("Applications fetched successfully!");
            } catch (err) {
                setError(err.message);
                toast.error("Failed to fetch applications!");
            } finally {
                setLoading(false);
            }
        };

        fetchApplications();
    }, [jobId, token]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <ToastContainer />
            <h1>Applications</h1>
            {applications.length === 0 ? (
                <p>No applications found.</p>
            ) : (
                <ul>
                    {applications.map(application => (
                        <li key={application._id} className="mb-4 p-4 border rounded shadow">
                            <h2 className="text-lg font-bold mb-2">{application.candidate?.user?.name || 'No Name Provided'}</h2>
                            <p><strong>Phone Number:</strong> {application.candidate?.phoneNumber || 'N/A'}</p>
                            <p><strong>Email:</strong> {application.candidate?.user?.email || 'N/A'}</p>
                            <p><strong>LinkedIn:</strong> <a href={application.candidate?.linkedinProfile} target="_blank" rel="noopener noreferrer">{application.candidate?.linkedinProfile || 'N/A'}</a></p>
                            <p><strong>Portfolio:</strong> <a href={application.candidate?.portfolio} target="_blank" rel="noopener noreferrer">{application.candidate?.portfolio || 'N/A'}</a></p>
                            <button
                                onClick={() => handleViewMore(application.candidate._id)}
                                className="mt-2 py-2 px-4 bg-indigo-600 text-white rounded shadow hover:bg-indigo-700"
                            >
                                View More Details
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

const handleViewMore = (candidateId) => {
    // Implement navigation or modal to show more details about the candidate
    console.log(`Fetch more details for candidate ID: ${candidateId}`);
};

export default ViewApplications;
