// src/components/ViewJob.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ViewJob = () => {
    const [job, setJob] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [applicationStatus, setApplicationStatus] = useState(null);
    const [progress, setProgress] = useState(0);
    const { jobId } = useParams();
    const userId = localStorage.getItem('user');
    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchJobDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/admin/view-job/${jobId}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setJob(response.data.job);
                setLoading(false);
            } catch (err) {
                setError('Failed to fetch job details');
                setLoading(false);
                console.error(err);
            }
        };

        fetchJobDetails();
    }, [jobId, token]);

    const handleApply = async () => {
        if (!userId) {
            toast.error('You must be logged in to apply for a job.');
            return;
        }

        setApplicationStatus('Submitting...');
        setProgress(10); // Set initial progress

        try {
            const response = await axios.post('http://localhost:5000/application/create-application', {
                candidate: userId,
                job: jobId
            }, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            setProgress(100); // Set progress to 100% upon success
            setApplicationStatus('Application submitted successfully!');
            toast.success('Application submitted successfully!');
            console.log(response.data);
        } catch (err) {
            setProgress(0); // Reset progress on failure
            setApplicationStatus('Failed to apply for the job');
            toast.error('Failed to apply for the job');
            console.error(err);
        }
    };

    if (loading) {
        return <div className="text-center py-4">Loading...</div>;
    }

    if (error) {
        toast.error(error);
        return <div className="text-center py-4">{error}</div>;
    }

    return (
        <div className="min-h-screen bg-gray-100 p-6">
            <ToastContainer />
            <h1 className="text-3xl font-bold mb-6">Job Details</h1>
            {job && (
                <div className="bg-white p-4 rounded-lg shadow-md">
                    <h2 className="text-xl font-semibold mb-2">{job.title}</h2>
                    <p className="text-gray-600 mb-2">Company: {job.company}</p>
                    <p className="text-gray-600 mb-2">Category: {job.category}</p>
                    <p className="text-gray-600 mb-2">Type: {job.type}</p>
                    <p className="text-gray-600 mb-2">Experience Required: {job.experienceRequired}</p>
                    <p className="text-gray-600 mb-2">Salary: {job.salary}</p>
                    <p className="text-gray-800 mb-4">{job.description}</p>
                    <button
                        onClick={handleApply}
                        className="w-full py-2 px-4 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
                        disabled={progress > 0 && progress < 100}
                    >
                        Apply
                    </button>
                    {progress > 0 && progress < 100 && (
                        <div className="mt-4">
                            <div className="relative pt-1">
                                <div className="flex mb-2 items-center justify-between">
                                    <div className="text-xs font-semibold inline-block py-1 px-2 rounded text-teal-600 bg-teal-200 uppercase last:mr-0 mr-1">
                                        {applicationStatus}
                                    </div>
                                </div>
                                <div className="flex">
                                    <div className="w-full bg-gray-200 rounded">
                                        <div
                                            className="text-xs font-semibold text-center text-white bg-teal-500 py-1 px-2 rounded"
                                            style={{ width: `${progress}%` }}
                                        >
                                            {progress}%
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default ViewJob;
