import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function AdminDashboard() {
    const navigate = useNavigate();
    const [jobs, setJobs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchJobs = async () => {
            const adminId = localStorage.getItem('user'); // Assuming admin ID is stored in local storage as 'user'
            const token = localStorage.getItem('token');

            try {
                const response = await axios.get(`http://localhost:5000/admin/${adminId}/jobs`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setJobs(response.data.jobs);
                setLoading(false);
            } catch (err) {
                setError('Failed to fetch jobs');
                setLoading(false);
                console.error(err);
            }
        };

        fetchJobs();
    }, []);

    const handleUpdate = (jobId) => {
        navigate(`/update-job/${jobId}`);
    };

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleDateString(); // Formats date as MM/DD/YYYY by default
    };

    if (loading) {
        return <div className="text-center py-4">Loading...</div>;
    }

    if (error) {
        toast.error(error);
        return <div className="text-center py-4">{error}</div>;
    }

    return (
        <div className="min-h-screen bg-gray-100 p-6">
            <ToastContainer />
            <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>
            <div className="flex flex-col space-y-4">
                <button
                    onClick={() => navigate('/create-job')}
                    className="w-full py-3 px-6 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
                >
                    Create Job
                </button>
                <button
                    onClick={() => navigate('/view-applications')}
                    className="w-full py-3 px-6 bg-green-600 text-white font-semibold rounded-lg shadow-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2"
                >
                    View Applications
                </button>
            </div>
            <div className="mt-6">
                <h2 className="text-2xl font-bold mb-4">Jobs Created by Admin</h2>
                <div className="bg-white p-4 rounded-lg shadow-md">
                    {jobs.length > 0 ? (
                        <ul className="space-y-4">
                            {jobs.map((job) => (
                                <li key={job._id} className="p-4 border border-gray-200 rounded-lg shadow-sm flex items-center justify-between">
                                    <div>
                                        <h3 className="text-xl font-semibold">{job.title}</h3>
                                        <p className="text-gray-600">Company: {job.company}</p>
                                        <p className="text-gray-600">Posted on: {formatDate(job.createdAt)}</p>
                                    </div>
                                    <button
                                        onClick={() => handleUpdate(job._id)}
                                        className="ml-4 py-2 px-4 bg-yellow-600 text-white font-semibold rounded-lg shadow-md hover:bg-yellow-700 focus:outline-none focus:ring-2 focus:ring-yellow-500 focus:ring-offset-2"
                                    >
                                        View Applications
                                    </button>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>No jobs created by this admin.</p>
                    )}
                </div>
            </div>
        </div>
    );
}

export default AdminDashboard;
