import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function ViewCandidate() {
    const { candidateId } = useParams();
    const [candidate, setCandidate] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [status, setStatus] = useState(''); // State for holding the selected status
    const [rejectionReason, setRejectionReason] = useState(''); // State for holding the rejection reason
    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchCandidateDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/candidate/view-candidate/${candidateId}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setCandidate(response.data.candidate);
                setStatus(response.data.candidate?.status || ''); // Set the current status
                setLoading(false);
                toast.success("Candidate details fetched successfully!");
            } catch (err) {
                setError('Failed to fetch candidate details');
                setLoading(false);
                console.error(err);
                toast.error("Failed to fetch candidate details!");
            }
        };

        fetchCandidateDetails();
    }, [candidateId, token]);

    // Helper function to format the date
    const formatDate = (dateString) => {
        if (!dateString) return 'N/A';
        const date = new Date(dateString);
        return date.toLocaleDateString(); // Default format based on locale
    };

    // Handler for updating the status
    const handleUpdateStatus = async () => {
        try {
            const updateData = { status };

            // Include the rejection reason if the status is "Rejected"
            if (status === "Rejected") {
                updateData.rejectionReason = rejectionReason;
            }

            const response = await axios.put(`http://localhost:5000/candidate/update-status/${candidateId}`, updateData, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            toast.success("Status updated successfully!");
            setCandidate({ ...candidate, status: response.data.status }); // Update the status in the state

            // Clear the rejection reason if the update was successful
            if (status !== "Rejected") {
                setRejectionReason('');
            }
        } catch (err) {
            console.error(err);
            toast.error("Failed to update status!");
        }
    };

    if (loading) {
        return <div className="text-center py-4">Loading...</div>;
    }

    if (error) {
        return <div className="text-center py-4">{error}</div>;
    }

    return (
        <div className="min-h-screen bg-gray-100 p-6">
            <ToastContainer />
            <h1 className="text-3xl font-bold mb-6">Candidate Details</h1>
            {candidate ? (
                <div className="bg-white p-4 rounded-lg shadow-md">
                    <p><strong>Phone Number:</strong> {candidate.phoneNumber || 'N/A'}</p>
                    <p><strong>Address:</strong> {candidate.address || 'N/A'}</p>
                    <p><strong>LinkedIn Profile:</strong> {candidate.linkedinProfile || 'N/A'}</p>
                    <p><strong>Portfolio:</strong> {candidate.portfolio || 'N/A'}</p>
                    <p><strong>Skills:</strong> {candidate.skills.join(', ') || 'N/A'}</p>
                    <p><strong>Experience:</strong> {candidate.experience.map(exp => (
                        <div key={exp._id}>
                            <p>Company: {exp.company || 'N/A'}</p>
                            <p>Role: {exp.role || 'N/A'}</p>
                            <p>Start Date: {formatDate(exp.startDate)}</p>
                            <p>End Date: {formatDate(exp.endDate)}</p>
                            <p>Description: {exp.description || 'N/A'}</p>
                        </div>
                    ))}</p>
                    <p><strong>Education:</strong> {candidate.education.map(edu => (
                        <div key={edu._id}>
                            <p>Institution: {edu.institution || 'N/A'}</p>
                            <p>Degree: {edu.degree || 'N/A'}</p>
                            <p>Start Date: {formatDate(edu.startDate)}</p>
                            <p>End Date: {formatDate(edu.endDate)}</p>
                            <p>Description: {edu.description || 'N/A'}</p>
                        </div>
                    ))}</p>

                    {/* Status Dropdown */}
                    <div className="my-4">
                        <label className="block text-gray-700">Update Status:</label>
                        <select
                            className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
                            value={status}
                            onChange={(e) => setStatus(e.target.value)}
                        >
                            <option value="">Select Status</option>
                            <option value="Applied">Applied</option>
                            <option value="In Review">In Review</option>
                            <option value="Interviewed">Interviewed</option>
                            <option value="Hired">Hired</option>
                            <option value="Rejected">Rejected</option>
                        </select>
                    </div>

                    {/* Rejection Reason Input (conditionally rendered) */}
                    {status === "Rejected" && (
                        <div className="my-4">
                            <label className="block text-gray-700">Rejection Reason:</label>
                            <textarea
                                className="mt-1 block w-full p-2 border border-gray-300 rounded-md"
                                value={rejectionReason}
                                onChange={(e) => setRejectionReason(e.target.value)}
                                placeholder="Provide the reason for rejection"
                            />
                        </div>
                    )}

                    {/* Update Status Button */}
                    <button
                        onClick={handleUpdateStatus}
                        className="w-full py-2 px-4 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
                    >
                        Update Status
                    </button>
                </div>
            ) : (
                <p>No candidate details available.</p>
            )}
        </div>
    );
}

export default ViewCandidate;
