import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ViewApplications = () => {
    const [applications, setApplications] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const userId = localStorage.getItem('user');
    const token = localStorage.getItem('token');

    useEffect(() => {
        const fetchApplications = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/application/application-id/${id}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setApplications(response.data.applications);
                setLoading(false);
            } catch (err) {
                setError('Failed to fetch applications');
                setLoading(false);
                console.error(err);
            }
        };

        fetchApplications();
    }, [userId, token]);

    if (loading) {
        return <div className="text-center py-4">Loading...</div>;
    }

    if (error) {
        toast.error(error);
        return <div className="text-center py-4">{error}</div>;
    }

    return (
        <div className="min-h-screen bg-gray-100 p-6">
            <ToastContainer />
            <h1 className="text-3xl font-bold mb-6">My Applications</h1>
            {applications.length === 0 ? (
                <div className="text-center py-4">No applications found.</div>
            ) : (
                <table className="w-full bg-white border border-gray-200 rounded-lg shadow-md">
                    <thead>
                        <tr className="bg-gray-200 text-left">
                            <th className="py-3 px-4 border-b">Job Title</th>
                            <th className="py-3 px-4 border-b">Status</th>
                            <th className="py-3 px-4 border-b">Submitted At</th>
                        </tr>
                    </thead>
                    <tbody>
                        {applications.map((application) => (
                            <tr key={application._id}>
                                <td className="py-3 px-4 border-b">{application.job.title}</td>
                                <td className="py-3 px-4 border-b">{application.status}</td>
                                <td className="py-3 px-4 border-b">{new Date(application.submittedAt).toLocaleDateString()}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default ViewApplications;
