import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useParams } from 'react-router-dom';

function ViewApplications() {
    const { jobId } = useParams();
    const [candidates, setCandidates] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        // Replace with your actual API endpoint
        const fetchCandidates = async () => {
            try {
                setLoading(true);
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:5000/application/candidates/${jobId}`, {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                setCandidates(response.data.candidates);
                console.log(response.data);
                toast.success("Candidates fetched successfully!");
            } catch (err) {
                setError(err.message);
                toast.error("Failed to fetch candidates!");
            } finally {
                setLoading(false);
            }
        };

        fetchCandidates();
    }, []); // Empty dependency array means this effect runs once when component mounts

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    const handleViewMore = (candidateId) => {
        // Handle fetching more detailed information when a candidate is selected
        console.log(`Fetch more details for candidate ID: ${candidateId}`);
        // For example, open a modal or navigate to a detailed view
    };

    return (
        <div>
            <h1>Candidates</h1>
            {candidates.length === 0 ? (
                <p>No candidates found.</p>
            ) : (
                <ul>
                    {candidates.map(candidate => (
                        <li key={candidate._id}>
                            <h2>{'No Name Provided'}</h2>
                            <p><strong>Phone Number:</strong> {candidate.phoneNumber || 'N/A'}</p>
                            <p><strong>Email:</strong> {candidate.email || 'N/A'}</p>
                            <button onClick={() => handleViewMore(candidate._id)}>View More Details</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default ViewApplications;
